// ==UserScript==
// @name         Cosmos
// @namespace    http://tampermonkey.net/
// @version      1.0
// @description  Custom page that displays the dashboard info of all the Sagemaker jobs you've worked on recently.
// @author       elgustav@
// @match        https://cw-dashboards.aka.amazon.com/cloudwatch/dashboardInternal?accountId=753462827423
// @icon         data:image/svg+xml;base64,
// @grant        none
// ==/UserScript==

let style= `
@keyframes starShine{
    0%{
        opacity:0.1;
    }
    50%{
        opacity:1;
    }
    100%{
        opacity:0.1;
    }
}

:root{
    color-scheme: dark;
}

#cwdb-container, #cwdb-container > *{
    zoom:0.1%;
    height:1rem !important;
    z-index:-3;
}

html, body{
    background: #090a13;
    background: rgb(9,8,23);
    background: linear-gradient(21deg, rgba(9,8,23,1) 0%, rgba(22,3,45,1) 32%, rgba(3,11,33,1) 65%, rgba(9,1,19,1) 96%);
    margin:0;
    padding:0;
}

body{
    display:flex;
    justify-content:center;
    z-index:-2;
}



.cosmos-background-star{
    background: rgb(255,255,255);
    background: radial-gradient(circle, rgba(255,255,255,1) 0%, rgba(0,0,0,0) 100%);
    border-radius:10rem;
    position:fixed;
    z-index:0 !important;
    animation:starShine 5s infinite;
    will-change:opacity;
}

#cosmos-container{
    display:flex;
    justify-content:center;
}

#cosmos-container > * {
    z-index:2;
}

#cosmos-logo{
    width:32rem;
    height:10rem;
    position:fixed;
    top:0rem;
    left:0rem;
}

* {
    color:white;
    font-family: "Inter", serif !important;
}



#cosmos-summary{
    display:flex;
    justify-content:center;
    align-items:center;
    flex-direction:column;
    background-color: #000617d0;
    border-radius: 0rem 0rem 5rem 5rem;
    width:50rem;
    height:13rem;
    position:fixed;
    z-index:3;
}

#cosmos-summary-title{
    margin: 1.5rem;
}

#cosmos-batches{
    margin-top:13rem;
    width:50rem;
}

.cosmos-job{
    display:flex;
    justify-content:center;
    align-items:center;
    flex-direction:column;
    width:50rem;
    border-radius:3rem;
    background-color: #000617d0;
    margin-top:1rem;
    height:9rem;
    z-index:2;
}

#cosmos-summary-values, .cosmos-job-values{
    display:flex;
    justify-content:space-evenly;
    align-items:center;
    border-top: 1px solid;
}

.cosmos-summary-value {
    display: flex;
    justify-content: left;
    align-items: center;
    flex-direction: column;
    margin: 2rem;
    width: 12rem;
    font-size: 1.75rem;
    margin-top: 1rem;
}

.cosmos-job-title{
    margin:1rem;
}

.cosmos-job-icon{
    width:2rem;
    height:2rem;
    position:absolute;
    margin-right:45rem;
    scale: (1,0.8);
}

.cosmos-job-value{
    display: flex;
    justify-content: left;
    align-items: center;
    flex-direction: column;
    margin: 2rem;
    margin-top: 1rem;
}

.cosmos-summary-label{
    font-weight:600;
    margin-bottom:0.5rem;
}

#cosmos-sidebar {
    position: fixed;
    right: 0rem;
    width: 35rem;
    height: 100%;
    background-color: #000617d0;
    border-radius: 2rem 0rem 0rem 2rem;
    display:flex;
    justify-content:left;
    align-items:center;
    flex-direction:column;
    gap:1rem;
    z-index:2;
}

#cosmos-sidebar-title{
    display:flex;
    justify-content:center;
    align-items:center;
    gap:1rem;
    border-bottom:1px solid white;
    width:80%;
    padding:1rem;
}

.cosmos-sidebar-details, .cosmos-sidebar-container{
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
}

.cosmos-sidebar-details-summary{
    user-select: none;
    margin-bottom: 0.5rem;
    cursor: pointer;
    font-weight: 600;
    font-size: 1.5rem;
    border-radius: 10rem;
    padding: 0.5rem;
}

.cosmos-sidebar-details-settings {
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
}

#cosmos-sidebar input, #cosmos-sidebar select {
    border: none;
    padding: 0.5rem;
    text-align: center;
    border-radius: 10rem;
    background-color: #00000099;
    margin: 0.5rem 0rem;
    border:2px solid #ffffffaa;
}

#cosmos-canvas{
    position:fixed;
    width:100%;
    height:100%;
    padding:0;
    margin:0;
}

`;

