function drawBackground() {
    let gradient = context.createLinearGradient(0, window.innerHeight * 0.1, window.innerWidth, window.innerWidth * 0.9);
    gradient.addColorStop(0, "rgba(9,8,23,1)");
    gradient.addColorStop(0.32, "rgba(22,3,45,1)");
    gradient.addColorStop(0.65, "rgba(3,11,33,1)");
    gradient.addColorStop(0.96, "rgba(9,1,19,1)");

    context.fillStyle = gradient;
    context.fillRect(0, 0, window.innerWidth, window.innerHeight);
}

function createStars(){
    stars = [];
    let starAmount = 1000;
    starAmount = Math.floor(window.innerWidth*window.innerHeight/2000);
    for(let i=0;i<starAmount;i++){
        let x=Math.floor(Math.random()*window.innerWidth);
        let y=Math.floor(Math.random()*window.innerHeight);
        let size = Math.floor(Math.random()*1.5)+1;
        let opacity = Math.random().toFixed(2);
        let opacityChangeRate = ((Math.floor(Math.random()*2000)-1000)/100000).toFixed(2);

        let star = {x,y,size,opacity,opacityChangeRate};
        stars.push(star);
    }
}

function drawStars() {
    for(let i=0;i<stars.length;i++){
        let star = stars[i];
        let radialGradient = context.createRadialGradient(star.x/2,star.y/2,star.size/2,star.x,star.y,star.size);
        radialGradient.addColorStop(0.5,"#ffffff");
        radialGradient.addColorStop(1,`rgba(255,255,255,${star.opacity})`);

        context.beginPath();
        context.arc(star.x,star.y,star.size,0,2*Math.PI);
        context.fillStyle = radialGradient;
        context.fill();

        star.opacity=Number(star.opacity)+Number(star.opacityChangeRate);
        if((star.opacity>=1)||(star.opacity<=0)){
            star.opacityChangeRate=star.opacityChangeRate*-1;
        }
    }
}

function setSuspiciousEntity(){
    let x=Math.floor(Math.random()*window.innerWidth);
    let y=Math.floor(Math.random()*window.innerHeight);
    suspiciousEntity.x = x;
    suspiciousEntity.y = y;
    let size = Math.floor(Math.random()*7)+3;
    suspiciousEntity.size=size;
}

function drawSuspiciousEntity(){
    for(let i=0;i<suspiciousReferencePoints.length;i++){
        let x = suspiciousReferencePoints[i][0]*suspiciousEntity.size*1.5+suspiciousEntity.x;
        let y = suspiciousReferencePoints[i][1]*suspiciousEntity.size*1.5+suspiciousEntity.y;

        let radialGradient = context.createRadialGradient(x/2,y/2,1.5,x,y,1.5);
        radialGradient.addColorStop(0.5,"#ffffff");
        radialGradient.addColorStop(1,`rgba(255,255,255,${suspiciousEntity.opacity})`);

        context.beginPath();
        context.arc(x,y,1.5,0,2*Math.PI);
        context.fillStyle = radialGradient;
        context.fill();

        if(suspiciousEntity.opacity<0){
            setSuspiciousEntity();
            suspiciousEntity.opacityChangeRate*=-1;

        }
        else if(suspiciousEntity.opacity>1){
            suspiciousEntity.opacityChangeRate*=-1;
        }

        suspiciousEntity.opacity=Number(suspiciousEntity.opacity)+Number(suspiciousEntity.opacityChangeRate);

    }
}

