function formatTime(time){
    if(!time){
        return "--";
    }
    let seconds = time;
    let minutes = 0;
    let hours = 0;
    let displayTime = Math.round(seconds)+"s";
    if(seconds>=60){
        minutes = seconds/60;
        if(minutes>=60){
            hours = seconds/3600;
            displayTime = hours.toFixed(2)+"h";
        }
        else{
            displayTime = minutes.toFixed(2)+"min";
        }
    }
    return displayTime;
}
